import React, { useState, useEffect } from 'react';
import './../App.css';
import { async } from 'q';

export default function UploadForm(props) {

    const [fileNotUploaded, setFileNotUploaded] = useState(false);
    const [fileName, setFileName] = useState('Name');
    const [jsonResp, setJsonResp] = useState('none');

    function onChangeHandler(event) {
        console.log(event.target.files[0]);
        // this.fileName = event.target.files[0].name;
        setFileName(event.target.files[0].name);
        setFileNotUploaded(true);
    }

    // function componentDidMount() {
    //     console.log('component did mount');
    //     fetch('http://localhost:8383/api/v1')
    //     .then(res => res.json())
    //     .then((data) => {
    //         console.log(data);
    //         setJsonResp(data);

    //     })
    //     .catch(console.log)
    // }

    useEffect(() => {
        const fetchData = async () => {
            const response = await fetch("http://localhost:8383/api/v1");
            const data = await response.json();
            console.log(data);
        }

        const uploadFileFunc = async () => {
            const resp = await fetch("http://localhost:8383/api/v1/someFileUpload", {
                method: 'POST',
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    firstParam: 'mark',
                    secondParam: 'zukerburg',
                })
            });
            const fileData = await resp.json();
            console.log(fileData);

        }

        fetchData();
    }, []);

    function uploadButtonClick () {
        alert("hi");
        uploadFileFunc();
    }

    return (
        <div className="container" >
            <div className="row justify-content-md-center">
                <div className="col-md-6">
                    <form>
                        <div className="form-group">
                            <input onChange={onChangeHandler} type="file" className="custom-file-input" id="customFile"/>
                            <label className="custom-file-label margin-top-50" htmlFor="customFile">{fileNotUploaded? <p> {fileName} </p>: <p> Choose file</p>}</label>
                        </div>
                        <div className="row margin-top-14 justify-content-md-center">
                            <button onClick={uploadButtonClick} className="btn btn-info">Upload</button>
                        </div>
                    </form>
                </div>
            </div>

        </div>
    )

}
