import React from 'react';
import './../App.css';

export default function UploadForm(props) {

    function uploadButtonClick () {
        alert("hi");
    }

    function onChangeHandler(event) {
        console.log(event.target.files[0]);
    }

    return (
        <div className="container" >
            <div className="row justify-content-md-center">
                <div className="col-md-4">
                    <form>
                        <div className="form-group">
                            <input onChange={onChangeHandler} type="file" className="custom-file-input" id="customFile"/>
                            <label className="custom-file-label margin-top-50" htmlFor="customFile"><p>Choose file</p><p>Some name</p></label>
                        </div>
                        <div className="row margin-top-14 justify-content-md-center">
                            <button onClick={uploadButtonClick} className="btn btn-info">Upload</button>
                        </div>
                    </form>
                </div>
            </div>


        </div>
    )

}
